INSERT INTO `flowtexts` (`id`, `matn`, `status`, `priority`, `user_id`, `created_at`, `updated_at`) VALUES
(1, 'مقام معظم رهبری (مدظله العالی) پدیده خواهران طلبه پدیده عظیم و مبارکی است. هزاران عالم ،\n                    پژوهشگر ، فقیه و فیلسوف در حوزه‌های علمی خواهران تربیت شوند این حرکت عظیمی خواهد بود.', 4, 1, 1, '2024-11-02 06:12:39', '2024-11-02 06:12:39'),
(2, 'الإمامُ الصّادقُ عليه السلام :مَن تَعَلَّمَ للّهِِ و عَمِلَ للّهِِ و عَلَّمَ للّهِِ دُعِيَ في مَلَكوتِ\n                    السَّماواتِ عَظيما، فقيلَ : تَعَلَّمَ للّهِِ ، و عَمِلَ للّهِِ ، و عَلَّمَ للّهِِ !', 4, 2, 1, '2024-11-02 06:13:26', '2024-11-02 06:13:26'),
(3, 'امام صادق علیه السلام هر که برای خدا علم بیاموزد و به آن عمل کند و به دیگران آموزش دهد\n                    در ملکوت آسمان‌ها به بزرگی یاد شود و گفته آید برای خدا آموخت برای خدا عمل کرد و برای خدا آموزش داد.\n                    الکافی ۱/۳۵/۶', 4, 3, 1, '2024-11-02 06:13:41', '2024-11-02 06:13:41');
